"use strict";

const WebpackError = require("../WebpackError");

const SizeFormatHelpers = require("../SizeFormatHelpers");

class MinMaxSizeWarning extends WebpackError {
  constructor(keys, minSize, maxSize) {
    let keysMessage = "Fallback cache group";

    if (keys) {
      keysMessage = keys.length > 1 ? "Cache groups ".concat(keys.sort().join(", ")) : "Cache group ".concat(keys[0]);
    }

    super("SplitChunksPlugin\n" + "".concat(keysMessage, "\n") + "Configured minSize (".concat(SizeFormatHelpers.formatSize(minSize), ") is ") + "bigger than maxSize (".concat(SizeFormatHelpers.formatSize(maxSize), ").\n") + "This seem to be a invalid optimiziation.splitChunks configuration.");
  }

}

module.exports = MinMaxSizeWarning;
