export default class ImageResize {

    imageResize = (imageUrl, maxWidth) => {

        let canvasUrl;
        let tempImg = new Image();
        tempImg.src = imageUrl;

        let createCanvas = (width, height, img) => {
            let canvas = document.createElement('canvas');
            canvas.width = width;
            canvas.height = height;
            let ctx = canvas.getContext('2d');
            ctx.drawImage(img, 0, 0, canvas.width, canvas.height)
            return canvas.toDataURL();
        }

        tempImg.onload = () => {
            let tempW = tempImg.width;
            let tempH = tempImg.height;
            let min = tempW >= tempH ? tempH : tempW
            maxWidth = maxWidth || min;

            if (tempW <= maxWidth && tempH <= maxWidth) {
                createCanvas(tempW, tempH, tempImg)
            }

            let startY = 0;
            if (min !== tempH) {
                startY = Math.round(tempH / 2 - (min / 2))
            }
            let startX = 0;
            if (min !== tempW) {
                startX = Math.round(tempW / 2 - (min / 2))
            }

            let finalImage = new Image();
            finalImage.src = this.getImagePortion(tempImg, min, min, startX, startY, 1);

            finalImage.onload = () => {
                canvasUrl = createCanvas(maxWidth, maxWidth, finalImage)

            }

        }

        return canvasUrl
    }

    getImagePortion(imgObj, newWidth, newHeight, startX, startY, ratio) {
        let tnCanvas = document.createElement('canvas');
        let tnContext = tnCanvas.getContext('2d');
        tnCanvas.width = newWidth;
        tnCanvas.height = newHeight;

        let bufferCanvas = document.createElement('canvas');
        let bufferContext = bufferCanvas.getContext('2d')
        bufferCanvas.width = imgObj.width;
        bufferCanvas.height = imgObj.height;

        bufferContext.drawImage(imgObj, 0, 0);

        tnContext.drawImage(bufferCanvas, startX, startY, newWidth * ratio, newHeight * ratio, 0, 0, newWidth, newHeight)
        return tnCanvas.toDataURL();


    }
}
