class SidePanelLeft extends React.Component{

    state = { activeItems: 'NewsFeed',
              channels: '',
              modalOpen : false,
              channelName: '',
              channelDetail: '',
              channelRef: firebase.database().ref('Channels'),
              user: this.props.currentUser,
              errors : [],


  };


    render(){
        const {activeItems,modalOpen, channels} = this.state;
        return(


            <Menu   
            fixed='left'
            vertical
            style={{ background: '#FFFFFF', top: '80px', padding: '30px 0' }}
            color='black'        
            size='large'
            stackable


            >


            <Menu.Item 
              style={{padding: '10px 0'}}
              as='a'
              name='Bảng Tin'
              active={activeItems === 'Bảng Tin'}
              onClick={this.handleItemClicked}
              fitted
            >

              {/* <span style={{marginLeft:'10px',height: '100% '}}>Bảng Tin</span> */}
              <Header textAlign='left'  color='blue'  as='h4'><Image style={{ margin:'0', padding: '0'}}  src={newsfeedIcon} /> Bảng Tin</Header>
              </Menu.Item>




          </Menu>



        )

    }
}

export default SidePanelLeft;
