import React from 'react'
import { Modal, Input, Button, Icon,Image } from 'semantic-ui-react';

class FileModal extends React.Component{
    state = {
        file: null,
        authorized: ['image/jpeg', 'image/png', 'image/webp'],
        files: [],
    }

    addFile = event =>{
        let loadFile =[]
        const file = event.target.files[0];
        const reader = new FileReader();
        reader.onload = event =>{
            loadFile.push({file: file, url: event.target.result})
            this.setState({files: this.state.files.concat(loadFile)},()=>{
                console.log(this.state.files)
            })
        }
        reader.readAsDataURL(file)





        // if(file){
        //     this.setState({file})
        // }
    }

    sendFile = () =>{
        const {files} = this.state;
        const {uploadFile, closeModal} = this.props

        if(files.length > 0){
            files.forEach(val =>{
                if(this.isAuthorized(val.file.type)){
                    const metadata = {contentType: val.file.type}
                    uploadFile(val.file, metadata);
                }
            })
            closeModal();
            this.clearFile();
        }

        // if(file){
        //     if(this.isAuthorized(file.type)){
        //         //send File
        //         const metadata = {contentType: file.type}
        //         uploadFile(file, metadata);
        //         closeModal();
        //         this.clearFile();
        //     }
        // }

    }

    clearFile = () => {
        this.setState({files: []})
    }

    isAuthorized = fileType => this.state.authorized.includes(fileType);

    render(){

        const {fileModal, closeModal} = this.props;
        const {files} = this.state;

       return(
            <Modal basic open={fileModal} onClose={closeModal}>
                <Modal.Header>
                    Select an image file
                </Modal.Header>
                <Modal.Content>
                    <Input 

                        fluid
                        label='File types: jpg, png'
                        name='file'
                        type='file'
                        onChange={this.addFile}
                    />
                </Modal.Content>
                <Modal.Actions>

                    <Button.Group>
                        <Button negative inverted onClick={closeModal}>
                            <Icon name='remove' /> Cancel
                        </Button>
                        <Button.Or />
                        <Button onClick={this.sendFile} inverted positive>
                            <Icon name='checkmark' />Upload
                        </Button>
                    </Button.Group>
                    {files.forEach(val =>(
                        <Image size='small' src={val.url} />
                    ))}

                </Modal.Actions>
            </Modal>
       );
    }
}

export default FileModal
