"use strict";

const WebpackError = require("../WebpackError");

const SizeFormatHelpers = require("../SizeFormatHelpers");

module.exports = class EntrypointsOverSizeLimitWarning extends WebpackError {
  constructor(entrypoints, entrypointLimit) {
    const entrypointList = entrypoints.map(entrypoint => "\n  ".concat(entrypoint.name, " (").concat(SizeFormatHelpers.formatSize(entrypoint.size), ")\n").concat(entrypoint.files.map(asset => "      ".concat(asset)).join("\n"))).join("");
    super("entrypoint size limit: The following entrypoint(s) combined asset size exceeds the recommended limit (".concat(SizeFormatHelpers.formatSize(entrypointLimit), "). This can impact web performance.\nEntrypoints:").concat(entrypointList, "\n"));
    this.name = "EntrypointsOverSizeLimitWarning";
    this.entrypoints = entrypoints;
    Error.captureStackTrace(this, this.constructor);
  }

};
