import React from 'react'
import { Modal, Input, Button, Icon,Image } from 'semantic-ui-react';

class FileModal extends React.Component{
    state = {
        authorized: ['image/jpeg', 'image/png', 'image/webp'],
        currentFiles: {
            file: null,
            url: ''
        },

    }


    addFile = event =>{


        const file = event.target.files[0];
        const reader = new FileReader();

        if(file){
            if(this.isAuthorized(file.type)){
                reader.onload = event =>{

                    this.setState({currentFiles: {file: file, url: event.target.result} })
                }
                reader.readAsDataURL(file)
            }
        }

    }




    sendFileToProp = () =>{

    const {fileStateToProp,closeModal} = this.props
        fileStateToProp(this.state.currentFiles);
        closeModal();

    }





    isAuthorized = fileType => this.state.authorized.includes(fileType);

    render(){

        const {fileModal, closeModal} = this.props;


       return(
            <Modal basic open={fileModal} onClose={closeModal}>
                <Modal.Header>
                    Select an image file
                </Modal.Header>
                <Modal.Content>
                    <Input 

                        fluid
                        label='File types: jpg, png'
                        name='file'
                        type='file'
                        onChange={this.addFile}
                    />
                </Modal.Content>
                <Modal.Actions>

                    <Button.Group>
                        <Button negative inverted onClick={closeModal}>
                            <Icon name='remove' /> Cancel
                        </Button>
                        <Button.Or />
                        <Button onClick={this.sendFileToProp} className='submit' inverted positive>
                            <Icon className='submit' name='checkmark' />Upload
                        </Button>
                    </Button.Group>


                </Modal.Actions>
            </Modal>
       );
    }
}

export default FileModal
