import React from 'react'
import {Header, Grid, Image, Dropdown} from 'semantic-ui-react'
import {connect} from 'react-redux'

class HeaderPanel extends React.Component{

    state ={
        currentUser: this.props.currentUser
    }

    render(){

        const {currentUser} = this.state;

        return(

            <Grid.Column floated='left' verticalAlign='middle'> 


            <Dropdown
                trigger={
                    <span>
                        <Image fluid spaced='right' avatar src='https://www.gravatar.com/avatar/376ff76b1cf9cca75006978f4b993e61?d=identicon' />
                        {currentUser.displayName}
                    </span>
                }
            >
                {console.log(this.props.currentUser)}
            </Dropdown>

            </Grid.Column>

        );

    }

}


const mapStateToProp = state =>({
    currentUser: state.user.currentUser
})

export default connect(mapStateToProp)(HeaderPanel);
