import {connect} from 'react-redux'
class Messages extends React.Component{



    render(){
        const style ={
            width:'300px',
            height: '350px',
            position: 'fixed',
            top: '60%',
            left: '80%'
        }
        return(
            <Segment stacked style={style} >
                <Header color='blue'  inverted as='h4'> <Icon name='comments' /> Messages</Header>
                <Menu vertical>
                    <Menu.Item >
                        <Image avatar src='https://www.gravatar.com/avatar/b4339e6c847f4f25822532330320548e?d=identicon' />name
                    </Menu.Item>

                </Menu>
            </Segment>
        )

    }

}

export default Messages
