    state = {
        authorized: ['image/jpeg', 'image/png', 'image/webp'],
        files: this.props,
        currentFiles: [],

    }


    addFile = event =>{

        let loadFile =[]
        const file = event.target.files[0];
        const reader = new FileReader();

        if(file){
