import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Switch, Route, withRouter } from 'react-router-dom'
import {createStore} from 'redux'
import {Provider,connect} from 'react-redux'
import {composeWithDevTools} from 'redux-devtools-extension'
import Spinner from './Spinner'

import './index.css';
import App from './components/App';
import Register from './components/auth/register'
import Login from './components/auth/login'
import * as serviceWorker from './serviceWorker';
import firebase from './firebase'
import  rootReducer from './reducers'
import {setUser, clearUser,setPosts} from './actions'

const store = createStore(rootReducer, composeWithDevTools())
const postLoaded = []
class Root extends React.Component{

    componentDidMount(){
        firebase.auth().onAuthStateChanged(user =>{
            if(user){
                this.props.setUser(user);
                this.props.history.push("/");

            }else{
                this.props.history.push('/login');
                this.props.clearUser();
            }
        })

        const postsRef = firebase.database().ref('posts')

        postsRef.on('child_added',snap =>{
            postsRef.child(snap.key).on('child_added',snap =>{
                postLoaded.push(snap.val())
            })
        })

        this.props.setPosts(postLoaded.sort((a,b)=>{
            return b.timestamp - a.timestamp
        }))


    }




    render(){
        return this.props.isLoading ? <Spinner /> : (

            <Switch> 
                <Route exact path='/' component={App} />
                <Route path='/register' component={Register} />
                <Route path='/login' component={Login} />
            </Switch>

        );
    }
}

const mapStateFromProps = state => ({
    isLoading: state.user.isLoading
})


const RootWithAuth = withRouter(connect(mapStateFromProps,{setUser,clearUser,setPosts})(Root))

ReactDOM.render( 
    <Provider store={store}>
    <Router>
        <RootWithAuth />
    </Router>
    </Provider>
    , document.getElementById('root'));


serviceWorker.register();
