"use strict";

const WebpackError = require("../WebpackError");

const SizeFormatHelpers = require("../SizeFormatHelpers");

module.exports = class AssetsOverSizeLimitWarning extends WebpackError {
  constructor(assetsOverSizeLimit, assetLimit) {
    const assetLists = assetsOverSizeLimit.map(asset => "\n  ".concat(asset.name, " (").concat(SizeFormatHelpers.formatSize(asset.size), ")")).join("");
    super("asset size limit: The following asset(s) exceed the recommended size limit (".concat(SizeFormatHelpers.formatSize(assetLimit), ").\nThis can impact web performance.\nAssets: ").concat(assetLists));
    this.name = "AssetsOverSizeLimitWarning";
    this.assets = assetsOverSizeLimit;
    Error.captureStackTrace(this, this.constructor);
  }

};
