import React from 'react'
import {Link} from 'react-router-dom'
import {Grid,Icon,Header,Segment, Form, Button,Message, Input, Select} from 'semantic-ui-react'
import firebase from '../../firebase'

class Register extends React.Component{

    state={
        email: '',
        password:'',
        errors: [],
        isLoading: false


    }

    handleChange = event =>{
        this.setState({[event.target.name]: event.target.value})
        this.isFormValid();    
    }

    handleSubmit = event =>{
        event.preventDefault();
       if(this.isFormValid){
        this.setState({isLoading: true})
        firebase.auth().createUserWithEmailAndPassword(this.state.email, this.state.password)
        .then(() => {
            this.setState({isLoading: false})
            console.log('craeted')
        })
        .catch(err =>{
            this.setState({errors: this.state.errors.concat(err) ,isLoading: false})
            this.state.errors.map((key, val) => console.log(val))
        })
       }
    }

    isPasswordValid = ({password}) =>{
        if(password.length < 5 ){
            return false;
        }else{
            return true;
        }
    }


    isFormEmpty = ({email,password}) =>{
        return !email.length || !password.length
    }


    isFormValid = () =>{
        let error;
        let errors= [];

        if(this.isFormEmpty(this.state)){
            return false;
        }else if(!this.isPasswordValid(this.state)){
            return false;
        }else{
            this.setState({errors: []});
            return true;
        }
    }

    render(){
        const {email, password, errors, isLoading} = this.state;
        return(

            <Grid verticalAlign="middle" textAlign="center" className='register'>
                <Grid.Column style={{maxWidth: 550}}>
                    <Header color='green' textAlign='center' as='h2'>
                        Register for CStudy
                    </Header>
                    <Form size='large' onSubmit={this.handleSubmit} >
                        <Segment stacked>
                            <Form.Input
                                name='email'
                                placeholder='Emal Address'
                                fluid
                                required
                                // error={{ content: 'Please enter your first name', pointing: 'below' }}
                                size='large'
                                type='text'
                                icon='mail'
                                iconPosition='left'
                                onChange={this.handleChange}
                                value={email}
                            />
                            <Form.Input 
                                name='password'
                                placeholder='Password'
                                fluid
                                required
                                size='large'
                                type='password'
                                icon='key'
                                iconPosition='left'
                                onChange={this.handleChange}
                                value={password}
                            />
                            <Button 
                            size='large' 
                            fluid 
                            color='green'
                            loading={isLoading}
                            >
                                Submit
                            </Button>
                            <Message>
                                You have an account ? <Link to='/Login'>Login</Link>
                            </Message>
                        </Segment>


                    </Form>

                </Grid.Column>
            </Grid>
        )
    }

}

export default Register
