import React from 'react'
import {Header, Grid, Image, Dropdown, Icon, Menu} from 'semantic-ui-react'
import {connect} from 'react-redux'
import firebase from '../../firebase'
import { isAbsolute } from 'path';

class HeaderPanel extends React.Component{

    state ={
        currentUser: this.props.currentUser,

    }

    dropDownOptions = () =>[
        {
            key : 'profile',
            text : <span onClick={this.handleProfile} ><Icon name='user outline' /> Profile</span>
        },

        {
            key: 'message',
            text :  <span onClick={this.handleMessage} ><Icon name='comments' />Message</span>
        },
        {
            key : 'signOut',
            text: <span onClick={this.handleSignOut}><Icon name='sign out' /> Sign Out</span>
        },

    ]

    handleSignOut = event =>{
        event.preventDefault();
        firebase.auth().signOut();
    }

    render(){

        const {currentUser} = this.state;


        return(
            // <Grid.Row color='black' style={{height: '80px'}}>
            // <Grid.Column verticalAlign='middle' width={5} floated='left'>
            //     <Header color='white' inverted as='h1'> <Icon name='code' color='white' inverted /> CChat</Header>

            // </Grid.Column>
            // <Grid.Column textAlign='right' floated='right' verticalAlign='middle' width={5}>  

            // <Dropdown


            //     pointing='top left'
            //     inline
            //     className='icon'
            //     trigger={
            //         <span>
            //             <Image spaced='right' avatar src={currentUser.photoURL} />
            //             {currentUser.displayName}
            //         </span>
            //     }
            //     options={this.dropDownOptions()}
            // >

            // </Dropdown>

            // </Grid.Column>



            // </Grid.Row>

            <Menu  fixed='top' color='black' inverted style={{height: '60px'}} >
                <Grid columns='equal' as='Segment' style={{width: '100%'}}>
                    <Grid.Column verticalAlign='middle' floated='left'  >
                        <Header color='white' inverted> <Icon name='code' color='white' /> Chat</Header>
                    </Grid.Column>
                    <Grid.Column color='white' verticalAlign='middle' textAlign='right'>

                    <Dropdown
                        style={{position: absolute, zIndex:1000}}
                        pointing='top left'
                        trigger={
                            <span style={{color: 'white',}}>
                                <Image avatar spaced='right' src={currentUser.photoURL} /> 
                                {currentUser.displayName}

                            </span>

                        }

                        options={this.dropDownOptions()}


                    >


                    </Dropdown>

                    </Grid.Column>
                </Grid>
            </Menu>
        );

    }

}


const mapStateToProp = state =>({
    currentUser: state.user.currentUser
})

export default connect(mapStateToProp)(HeaderPanel);
