import React from 'react'
import { Menu, Image, Header } from 'semantic-ui-react'
import firebase from '../../firebase'
import newsfeedIcon from '../../Images/newsfeed.svg'


class SidePanelLeft extends React.Component{

    state = { activeItems: 'NewsFeed',
              channels: '',
              modalOpen : false,
              channelName: '',
              channelDetail: '',
              channelRef: firebase.database().ref('Channels'),
              user: this.props.currentUser,
              errors : [],


  };


    render(){
        const {activeItems,modalOpen, channels} = this.state;
        return(


            <Menu   
            fixed='left'
            vertical
            style={{ background: '#e7e7e7', top: '80px', padding: '30px 0' }}
            color='black'
            pointing
            borderless
            size='huge'
            compact


            >


            <Menu.Item 
              as='a'
              name='Bảng Tin'
              active={activeItems === 'Bảng Tin'}
              onClick={this.handleItemClicked}

            >

              {/* <span style={{marginLeft:'10px',height: '100% '}}>Bảng Tin</span> */}
              <Header color='blue' as='h4'><Image   src={newsfeedIcon} /> Bảng Tin</Header>
              </Menu.Item>




          </Menu>



        )

    }
}

export default SidePanelLeft;
