"use strict";

const _require = require("webpack-sources"),
      ConcatSource = _require.ConcatSource;

class WebWorkerChunkTemplatePlugin {
  apply(chunkTemplate) {
    chunkTemplate.hooks.render.tap("WebWorkerChunkTemplatePlugin", (modules, chunk) => {
      const chunkCallbackName = chunkTemplate.outputOptions.chunkCallbackName;
      const globalObject = chunkTemplate.outputOptions.globalObject;
      const source = new ConcatSource();
      source.add("".concat(globalObject, "[").concat(JSON.stringify(chunkCallbackName), "](").concat(JSON.stringify(chunk.ids), ","));
      source.add(modules);
      source.add(")");
      return source;
    });
    chunkTemplate.hooks.hash.tap("WebWorkerChunkTemplatePlugin", hash => {
      hash.update("webworker");
      hash.update("3");
      hash.update("".concat(chunkTemplate.outputOptions.chunkCallbackName));
      hash.update("".concat(chunkTemplate.outputOptions.globalObject));
    });
  }

}

module.exports = WebWorkerChunkTemplatePlugin;
