import React from 'react'
import {Link} from 'react-router-dom'
import {Grid,Icon,Header,Segment, Form, Button,Message, Input, Select} from 'semantic-ui-react'

class Register extends React.Component{

    state={
        email: '',
        password:'',
        passwordConfirmation: '',
        genderOptions:[
            {key: 'm', value: 'male', text: 'Male' },
            {key: 'f', value: 'female', text: 'Female'},
            {key: 'o', value: 'other', text: 'Other'}
        ],
        gender: '',
        errors: [],


    }

    handleChange = event =>{
        this.setState({[event.target.name]: event.target.value})
        console.log(this.isFormEmpty(this.state.email,this.state.password,this.state.passwordConfirmation))
    }

    genderChange = event =>{
        this.setState({gender: event.target.value})
    }

    isEmailValid = email =>{
        if(email){

        }
    }

    isFormEmpty = (email,password,passwordConfirmation) =>{
        if(email.length < 0 && password.length < 0 && passwordConfirmation.length < 0 ){
            return true
        }else{
            return false
        }
    }

    render(){
        const {email, password, passwordConfirmation, genderOptions, gender} = this.state;
        return(

            <Grid verticalAlign="middle" textAlign="center" className='register'>
                <Grid.Column style={{maxWidth: 450}}>
                    <Header color='green' textAlign='center' as='h2'>
                        Register for CStudy
                    </Header>

                    <Form size='large'>
                        <Segment stacked>
                            <Form.Input
                                name='email'
                                placeholder='Emal Address'
                                fluid
                                // error={{ content: 'Please enter your first name', pointing: 'below' }}
                                size='large'
                                type='text'
                                icon='mail'
                                iconPosition='left'
                                onChange={this.handleChange}
                                value={email}
                            />
                            <Form.Input 
                                name='password'
                                placeholder='Password'
                                fluid
                                required
                                size='large'
                                type='password'
                                icon='key'
                                iconPosition='left'
                                onChange={this.handleChange}
                                value={password}
                            />
                            <Form.Input 
                                name="passwordConfirmation"
                                placeholder="Password Confirmation"
                                fluid
                                required
                                size='large'
                                type='password'
                                icon='repeat'
                                iconPosition='left'
                                onChange={this.handleChange}
                                value={passwordConfirmation}
                            />
                            <Form.Field control={Select} placeholder='Gender' options={genderOptions} />
                            {console.log(gender)}
                            <Button size='large' fluid color='green'>Submit</Button>
                            <Message>
                                You have an account ? <Link to='/Login'>Login</Link>
                            </Message>
                        </Segment>


                    </Form>

                </Grid.Column>
            </Grid>
        )
    }

}

export default Register
