"use strict";

const _require = require("webpack-sources"),
      ConcatSource = _require.ConcatSource;

class JsonpHotUpdateChunkTemplatePlugin {
  apply(hotUpdateChunkTemplate) {
    hotUpdateChunkTemplate.hooks.render.tap("JsonpHotUpdateChunkTemplatePlugin", (modulesSource, modules, removedModules, hash, id) => {
      const source = new ConcatSource();
      source.add("".concat(hotUpdateChunkTemplate.outputOptions.hotUpdateFunction, "(").concat(JSON.stringify(id), ","));
      source.add(modulesSource);
      source.add(")");
      return source;
    });
    hotUpdateChunkTemplate.hooks.hash.tap("JsonpHotUpdateChunkTemplatePlugin", hash => {
      hash.update("JsonpHotUpdateChunkTemplatePlugin");
      hash.update("3");
      hash.update("".concat(hotUpdateChunkTemplate.outputOptions.hotUpdateFunction));
      hash.update("".concat(hotUpdateChunkTemplate.outputOptions.library));
    });
  }

}

module.exports = JsonpHotUpdateChunkTemplatePlugin;
