import React from 'react'
import { Modal, Header, Icon,Form,Image } from 'semantic-ui-react';

class CreatePostModal extends React.Component{
    render(){
        const {user} = this.props;
        return(
            <Modal centered size='small' open={true} closeIcon style={{top: '10%', transform: 'translateY(-10%)'}}>
                <Modal.Header >
                    <Header as='h3'><Icon name='write'/>Tạo Bài Viết</Header>
                </Modal.Header>
                <Modal.Content>
                    <Form style={{marginTop: '5px'}}>
                        <Form.Group >
                            <Image inline size='tiny' avatar src={user.photoURL}/>


                            <Form.Field style={{width: '100%'}}>
                                <Form.Input placeholder="Bạn đang nghĩ gì ?" style={{height: '70px',fontSize: 16}}  />
                            </Form.Field>




                        </Form.Group>
                        <Form.Button fluid style={{background: '##6ed18a'}}>Post</Form.Button>

                    </Form>
                </Modal.Content>
            </Modal>
        );
    }
}

export default CreatePostModal
