import React from 'react'
import {Link} from 'react-router-dom'
import {Grid,Icon,Header,Segment, Form, Button,Message, Input, Select} from 'semantic-ui-react'

class Register extends React.Component{

    state={
        email: '',
        password:'',
        passwordConfirmation:'',
        genderOptions:[
            {key: 'm', value: 'male', text: 'Male' },
            {key: 'f', value: 'female', text: 'Female'},
            {key: 'o', value: 'other', text: 'Other'}
        ],
        gender: '',
        errors: [],


    }

    handleChange = event =>{
        this.setState({[event.target.name]: event.target.value})
        this.isFormValid();


    }


    isPasswordValid = ({password, passwordConfirmation}) =>{
        if(password.length < 6 || passwordConfirmation <6 ){
            return false;
        }else if(password !== passwordConfirmation){
            return false;
        }else{
            return true;
        }
    }

    isPasswordConfirmValid = ({password, passwordConfirmation}) =>{
        if(passwordConfirmation.length < 5){
            return false;
        }else if(password !== passwordConfirmation){
            return false;
        }else{
            return true;
        }
    }

    isFormEmpty = ({email,password,passwordConfirmation}) =>{
        return !email.length || !password.length || !passwordConfirmation.length
    }


    isFormValid = () =>{
        let error;
        let errors= [];

        if(this.isFormEmpty(this.state)){
            return false;
        }else if(!this.isPasswordValid(this.state)){
            error = {message: 'Password Invalid'};
            this.setState({errors: errors.concat(error)});
            return false;
        }else{
            this.setState({errors: []});
            return true;
        }
    }

    submit = () =>{
        console.log('ok')
    }


    render(){
        const {email, password, passwordConfirmation, genderOptions, gender, errors} = this.state;
        return(

            <Grid verticalAlign="middle" textAlign="center" className='register'>
                <Grid.Column style={{maxWidth: 450}}>
                    <Header color='green' textAlign='center' as='h2'>
                        Register for CStudy
                    </Header>
                    {console.log(errors)}
                    <Form size='large' onSubmit={this.submit}>
                        <Segment stacked>
                            <Form.Input
                                name='email'
                                placeholder='Emal Address'
                                fluid
                                required
                                // error={{ content: 'Please enter your first name', pointing: 'below' }}
                                size='large'
                                type='text'
                                icon='mail'
                                iconPosition='left'
                                onChange={this.handleChange}
                                value={email}
                            />
                            <Form.Input 
                                name='password'
                                placeholder='Password'
                                fluid
                                required
                                size='large'
                                type='password'
                                icon='key'
                                iconPosition='left'
                                onChange={this.handleChange}
                                value={password}
                            />
                            <Form.Input 
                                name="passwordConfirmation"
                                placeholder="Password Confirmation"
                                fluid
                                required
                                size='large'
                                type='password'
                                icon='repeat'
                                iconPosition='left'
                                onChange={this.handleChange}
                                value={passwordConfirmation}
                            />
                            <Form.Field control={Select} placeholder='Gender' options={genderOptions} />
                            {console.log(gender)}
                            <Button size='large' fluid color='green'>Submit</Button>
                            <Message>
                                You have an account ? <Link to='/Login'>Login</Link>
                            </Message>
                        </Segment>


                    </Form>

                </Grid.Column>
            </Grid>
        )
    }

}

export default Register
