import React from 'react'
import {Header, Grid, Image, Dropdown} from 'semantic-ui-react'
import {connect} from 'react-redux'
import firebase from '../../firebase'

class HeaderPanel extends React.Component{

    state ={
        currentUser: this.props.currentUser,

    }

    dropDownOptions = () =>[
        {
            key : 'userSetting',
            text : <span>User Settings</span>
        },
        {
            key : 'signOut',
            text: <span onClick={this.handleSignOut}>Sign Out</span>
        }
    ]

    handleSignOut = event =>{
        event.preventDefault();
        firebase.auth().signOut();
    }

    render(){

        const {currentUser} = this.state;


        return(
            <Grid.Row>
            <Grid.Column color='green' width={3}>
                column 1
            </Grid.Column>
            <Grid.Column color='blue' width={9}>
                column 2
            </Grid.Column>

            <Grid.Column floated='right' color='olive' width={4}> 

            <Dropdown

                style={{marginLeft: '32em'}}
                pointing='top left'
                inline
                className='icon'
                trigger={
                    <span>
                        <Image spaced='right' avatar src={currentUser.photoURL} />
                        {currentUser.displayName}
                    </span>
                }
                options={this.dropDownOptions()}
            >

            </Dropdown>

            </Grid.Column>



            </Grid.Row>
        );

    }

}


const mapStateToProp = state =>({
    currentUser: state.user.currentUser
})

export default connect(mapStateToProp)(HeaderPanel);
